export const getAllTennisTournaments = (): Tournament[] => {
    return [...TENNIS_TOURNAMENTS_INTERNATIONAL, ...Object.values(TENNIS_TOURNAMENTS_BY_CATEGORY).flat()];
};

export const getTennisTournamentsByCategory = (category: string): Tournament[] => {
    return TENNIS_TOURNAMENTS_BY_CATEGORY[category] || [];
};

export const getTennisTournamentsByCountry = (countryId: string): Tournament[] => {
    return TENNIS_TOURNAMENTS_BY_CATEGORY[countryId] || [];
};

export const getInternationalTennisTournaments = (): Tournament[] => {
    return TENNIS_TOURNAMENTS_INTERNATIONAL;
};
